mapper.update:
UPDATE MpFind
SET mpfTitle = #{mpfTitle}
	,mpfContent = #{mpfContent}
	,mpfInfoDate = #{mpfInfoDate}
	,mpfStatusCode = #{mpfStatusCode}
	,mpfLocalCode = #{mpfLocalCode}
	,mpfPetType = #{mpfPetType}
	 
     ,mpfAddFile = #{mpfAddFile}
     
 WHERE mpfNo = #{mpfNo}

UPDATE MpFind
SET mpfDelYn = 'Y'
WHERE mpfNo = #{mpfNo}
