mapper.update:
UPDATE MpReport
SET mprTitle = #{mprTitle}
	,mprContent = #{mprContent}
	,mprInfoDate = #{mprInfoDate}
	,mprStatusCode = #{mprStatusCode}
	,mprLocalCode = #{mprLocalCode}
	,mprPetType = #{mprPetType}
	,mprGen = #{mprGen}
	
    ,mprAddFile = #{mprAddFile}
    
    ,mprLocalDetail = #{mprLocalDetail}
 WHERE mprNo = #{mprNo}

UPDATE MpReport
SET mprDelYn = 'Y'
WHERE mprNo = #{mprNo}
