mapper.insert:
INSERT INTO mhreport(mhrNo, mhrTitle
									, mhrContent, mhrRegDate
									, mhrInfoDate, mhrName
									, mhrMage, mhrage
									, mhrNation, mhrIllCode
									, mhrStatusCode, mhrLocalCode
									, mhrWriter, mmNo
									, mhrGen, mhrHeight
									, mhrWeight, mhrPhysical
									, mhrFace, mhrHairColor
									, mhrHair, mhrWear
									
									,mhrAddFile
									
									)
	VALUES(MHREPORT_seq.NEXTVAL, #{mhrTitle}
				, #{mhrContent}, SYSTIMESTAMP
				, #{mhrInfoDate}, #{mhrName}
				, #{mhrMage}, #{mhrage}
				, #{mhrNation}, #{mhrIllCode}
				, #{mhrStatusCode}, #{mhrLocalCode}
				, #{mhrWriter}, #{mmNo}
				, #{mhrGen}, #{mhrHeight, jdbcType=VARCHAR}
				, #{mhrWeight, jdbcType=VARCHAR}, #{mhrPhysical, jdbcType=VARCHAR}
				, #{mhrFace, jdbcType=VARCHAR}, #{mhrHairColor, jdbcType=VARCHAR}
				, #{mhrHair, jdbcType=VARCHAR}, #{mhrWear, jdbcType=VARCHAR}
				
				, #{mhrAddFile}
				
				)

mapper.update:
UPDATE mhreport SET mhrTitle=#{mhrTitle}, mhrContent=#{mhrContent}, mhrName=#{mhrName}, mhrMage=#{mhrMage}, mhrage=#{mhrage}, mhrInfoDate=#{mhrInfoDate}, mhrHeight=#{mhrHeight, jdbcType=VARCHAR}, mhrWeight=#{mhrWeight, jdbcType=VARCHAR}, mhrPhysical=#{mhrPhysical, jdbcType=VARCHAR}, mhrFace=#{mhrFace, jdbcType=VARCHAR}, mhrHairColor=#{mhrHairColor, jdbcType=VARCHAR}, mhrHair=#{mhrHair, jdbcType=VARCHAR}, mhrWear=#{mhrWear, jdbcType=VARCHAR}
,mhrAddFile = #{mhrAddFile}
WHERE mhrNo=#{mhrNo}
