mapper.update:
UPDATE MREQUEST
SET mreReplyCode = '02'
WHERE mreNo = #{mreNo}

UPDATE MREQUEST
SET mreReplyCode = '01'
WHERE mreNo = #{mreNo}

UPDATE MREQUEST
SET mreRecoCnt = mreRecoCnt + 1
WHERE mreNo = #{mreNo}

mapper.select:
SELECT mreRecoCnt
FROM MREQUEST
WHERE mreNo= #{mreNo}

mapper.update:
UPDATE MREQUEST SET mmNo = #{mmNo}, mreWriter = #{mreWriter}, mreTitle = #{mreTitle}, mreContent = #{mreContent},
mreAddFile = #{mreAddFile},
mreRegDate = SYSTIMESTAMP WHERE mreNo = #{mreNo}

UPDATE MREQUEST
SET mreDelYn = 'Y'
WHERE mreNo = #{mreNo}

UPDATE MREQUEST
SET mreReadCnt =	(
						SELECT	MAX(mreReadCnt)
						FROM MREQUEST
						WHERE mreNo = #{mreNo}
					) +1
WHERE mreNo = #{mreNo}
